                gap: 20px;
            }
        }

        &__updates {
            width: 100%;
            @include flex-layout();

            input {
                width: 60%;
                border-radius: 20px;
                line-height: 2;
                padding-right: 20px;
                padding-left: 10px;
                border: none;
                font-size: 16px;

                &:focus {
                    border: none;
                    outline: none;
                }
            }

            button {
                width: 25%;
                line-height: 2;
                background-color: $bright-red;
                color: white;
                padding: 5px 10px;
            }
        }
    }

    &__rights {
        text-align: center;
        padding-top: 20px;
        color: $dark-grayish-blue;
        font-size: 14px;
    }
}
